const items = $input.all();

return items.map(item => {
  let raw = item.json.output;

  raw = raw.replace(/```json|```/g, '').trim();

  let parsed;
  try {
    parsed = JSON.parse(raw);
  } catch (error) {
    parsed = { error: 'Invalid JSON format', details: error?.message, raw };
  }

  return {
    json: parsed
  };
});

let path = null
let statusCode = null
let headers = null
let items = null
let message = null
for (const item of $input.all()) {

  if(item.json.statusCode){
    statusCode = item.json.statusCode
  }

  if(item.json.path){
    path = item.json.path
  }

  if(item.json.headers){
    headers = {...item.json.headers}
  }

  if(item.json.items){
    items = item.json.items
  }

  if(item.json.message){
    message = item.json.message
  }
}

return {
  json: {
    path: path,
    statusCode: statusCode,
    headers: headers,
    items: items,
    message: message
  }
}

